import React, { useState } from "react";
import "./Carousel.css";
import Card from "./Card";


/** Carousel: displays images and arrows to navigate through them
 * 
 * Props:
 * - photos: array of {src, caption} objects
 * - title: string describing the collection of images
 * 
 * State:
 * - currCardIdx: integer for current card index
 * 
 * App --> Carousel --> Card
 */
 function Carousel({ photos, title }) {
  const [currCardIdx, setCurrCardIdx] = useState(0);

  const currCard = photos[currCardIdx];
  const total = photos.length;

  // Move to the next card, with boundary checking
  function goForward() {
      setCurrCardIdx(currCardIdx => (currCardIdx + 1) % total);

  }
  //FIXED


  // Move to the previous card, with boundary checking
  function goBack() {
    setCurrCardIdx(currCardIdx => (currCardIdx - 1 + total) % total);
  }
  //FIXED

  return (
    <div className="Carousel">
      <h1>{title}</h1>
      <div className="Carousel-main">
        <i
          className={`bi bi-arrow-left-circle ${currCardIdx === 0 && "disabled"}`}
          onClick={goBack} id="left"
        />
        <Card
          caption={currCard.caption}
          src={currCard.src}
          currNum={currCardIdx + 1}
          totalNum={total}
        />
        <i
          className={`bi bi-arrow-right-circle ${currCardIdx === total - 1 && "disabled"}`}
          onClick={goForward}
        />
      </div>
    </div>
  );
}

export default Carousel;
